import { combineReducers, applyMiddleware, configureStore } from '@reduxjs/toolkit'
import thunk from "redux-thunk";
import { composeWithDevTools } from 'redux-devtools-extension';
import { prodcutListReducer } from "./redux/reducers/productReducer"

// here we combine all the reducers/actions
const reducer = combineReducers({
    prodcutList: prodcutListReducer,
});

// here we create a global state that can be accessed from anywhere in the app.
const initialState = {};

// thunk is a middleware which combine the redux and react together
const middleware = [thunk];


//create store
const store = configureStore(
    reducer,
    initialState,
    composeWithDevTools(applyMiddleware(...middleware))
);

export default store;
