import React, { useEffect } from 'react';
import { useDispatch, useSelector } from "react-redux"
import { Row, Col, Form, Button, Card, Image, ListGroup } from "react-bootstrap"
import { addToCart } from "../redux/actions/cartAction"
import { useParams, useNavigate, useLocation } from "react-router-dom"

const CartScreen = () => {

    const { id } = useParams();
    const navigate = useNavigate();
    const location = useLocation();

    const dispatch = useDispatch();


    const qty = location.search ? Number(location.search.split("=")[1]) : 1  //it will give us "qty" from url
    console.log("qty::", qty, id);

    useEffect(() => {
        if (id) {
            console.log('checking is dispatch event is firing');
            dispatch(addToCart(id, qty))
        }
    }, [id, qty])

    const cart = useSelector(state => state.cart)
    const { cartItems } = cart
    console.log("123", cartItems);

    return (
        <div>
            <h1>Component</h1 >
        </div>
    )
}

export default CartScreen;
