import React from 'react'
import products from '../products';
import { Row, Col, ListGroup, ListGroupItem, Button, Image } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import Rating from '../components/Rating';


const ProductDetails = ({ match }) => {

    const { id } = useParams("");
    // here we are matching the id of project from the id given in URL
    const product = products.find((p) => p._id === id)

    console.log(product.image, product._id);

    return (
        <div>
            <Row>
                <Col md={6}>
                    <Image src={product.image} alt={product.name} fluid={true} />
                </Col>

                <Col md={3}>
                    <ListGroup variant='flush'>
                        <ListGroupItem>
                            <h3> {product.name} </h3>
                        </ListGroupItem>

                        <ListGroupItem>
                            <Rating value={product.rating} text={`${product.numReviews} reviews`} />
                        </ListGroupItem>

                        <ListGroupItem>
                            Price : ${product.price}
                        </ListGroupItem>

                        <ListGroupItem>
                            {product.description}
                        </ListGroupItem>
                    </ListGroup>
                </Col>

                <Col md={3}>
                    <ListGroupItem>
                        <Row>
                            <Col> Status: </Col>
                            <Col> {product.countInStock > 0 ? 'In stock' : 'Out of stock'} </Col>
                        </Row>
                    </ListGroupItem>

                    <ListGroupItem>
                        <Button>

                        </Button>
                    </ListGroupItem>
                </Col>
            </Row>
        </div>
    )
}

export default ProductDetails;
